import React, {useEffect, useState} from 'react';
import API from '../api';
import { useParams, useNavigate } from 'react-router-dom';
import { Typography, Button } from '@mui/material';

export default function EventDetails(){
  const { id } = useParams();
  const [ev, setEv] = useState(null);
  const navigate = useNavigate();
  useEffect(()=>{ API.get('/api/events/'+id).then(r=>setEv(r.data)); },[id]);
  const book = async () => {
    const token = localStorage.getItem('token');
    if(!token) return navigate('/login');
    await API.post('/api/bookings', { eventId: id, seats: 1 }, { headers: { Authorization: 'Bearer '+token } });
    alert('Booked — check bookings page');
    navigate('/bookings');
  };
  if(!ev) return <div>Loading...</div>;
  return (
    <>
      <Typography variant="h4">{ev.title}</Typography>
      <Typography>{ev.description}</Typography>
      <Typography>Location: {ev.location}</Typography>
      <Typography>Date: {new Date(ev.date).toLocaleString()}</Typography>
      <Button variant="contained" onClick={book}>Book</Button>
    </>
  )
}
