import React, {useEffect, useState} from 'react';
import API from '../api';
import { useParams, useNavigate } from 'react-router-dom';
import { Typography, Button, Dialog, DialogTitle, DialogContent, DialogActions, Alert } from '@mui/material';
import { useAuth } from '../contexts/AuthContext';

export default function EventDetails(){
  const { id } = useParams();
  const [ev, setEv] = useState(null);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { user } = useAuth();

  useEffect(()=>{ 
    API.get('/api/events/'+id).then(r=>setEv(r.data)); 
  },[id]);
  const book = async () => {
    const token = localStorage.getItem('token');
    if(!token) return navigate('/login');
    await API.post('/api/bookings', { eventId: id, seats: 1 }, { headers: { Authorization: 'Bearer '+token } });
    alert('Booked — check bookings page');
    navigate('/bookings');
  };

  const handleDeleteEvent = async () => {
    try {
      const token = localStorage.getItem('token');
      if(!token) return navigate('/login');

      await API.delete('/api/events/'+id, { headers: { Authorization: 'Bearer '+token } });
      alert('Event deleted successfully');
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to delete event');
    }
    setDeleteDialogOpen(false);
  };

  const isOrganizer = user && ev && user._id === ev.organizer;
  if(!ev) return <div>Loading...</div>;
  return (
    <>
      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
      <Typography variant="h4">{ev.title}</Typography>
      <Typography>{ev.description}</Typography>
      <Typography>Location: {ev.location}</Typography>
      <Typography>Date: {new Date(ev.date).toLocaleString()}</Typography>
      <Typography>Capacity: {ev.capacity}</Typography>
      <Typography>Price: ${ev.price}</Typography>
      <Typography>Attendees: {ev.attendeesCount}</Typography>

      <div style={{ marginTop: '20px', display: 'flex', gap: '10px' }}>
        <Button variant="contained" onClick={book}>Book Event</Button>
        {isOrganizer && (
          <Button 
            variant="contained" 
            color="error" 
            onClick={() => setDeleteDialogOpen(true)}
          >
            Delete Event
          </Button>
        )}
      </div>

      <Dialog open={deleteDialogOpen} onClose={() => setDeleteDialogOpen(false)}>
        <DialogTitle>Delete Event</DialogTitle>
        <DialogContent>
          <Typography>
            Are you sure you want to delete "{ev.title}"? This action cannot be undone.
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteDialogOpen(false)}>Cancel</Button>
          <Button onClick={handleDeleteEvent} color="error" variant="contained">
            Delete
          </Button>
        </DialogActions>
      </Dialog>
    </>
  )
}
