import React, {useEffect, useState} from 'react';
import API from '../api';
import { useParams, useNavigate } from 'react-router-dom';
import { Typography, Button, Dialog, DialogTitle, DialogContent, DialogActions, Alert } from '@mui/material';

export default function EventDetails(){
  const { id } = useParams();
  const [ev, setEv] = useState(null);
  const [user, setUser] = useState(null);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(()=>{ 
    API.get('/api/events/'+id).then(r=>setEv(r.data)); 
    // Get current user info
    const token = localStorage.getItem('token');
    if(token) {
      API.get('/api/auth/profile', { headers: { Authorization: 'Bearer '+token } })
        .then(r=>setUser(r.data))
        .catch(()=>setUser(null));
    }
  },[id]);
  const book = async () => {
    const token = localStorage.getItem('token');
    if(!token) return navigate('/login');
    await API.post('/api/bookings', { eventId: id, seats: 1 }, { headers: { Authorization: 'Bearer '+token } });
    alert('Booked — check bookings page');
    navigate('/bookings');
  };
  if(!ev) return <div>Loading...</div>;
  return (
    <>
      <Typography variant="h4">{ev.title}</Typography>
      <Typography>{ev.description}</Typography>
      <Typography>Location: {ev.location}</Typography>
      <Typography>Date: {new Date(ev.date).toLocaleString()}</Typography>
      <Button variant="contained" onClick={book}>Book</Button>
    </>
  )
}
