import React, {useState} from 'react';
import API from '../api';
import { TextField, Button, Typography } from '@mui/material';
import { useNavigate, Link } from 'react-router-dom';

export default function Login(){
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const submit = async () => {
    try {
      const res = await API.post('/api/auth/login', { email, password });
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      navigate('/');
    } catch (err) {
      alert(err.response?.data?.message || err.message);
    }
  };
  return (
    <>
      <Typography variant="h5">Login</Typography>
      <TextField label="Email" value={email} onChange={e=>setEmail(e.target.value)} fullWidth sx={{mt:2}}/>
      <TextField label="Password" value={password} onChange={e=>setPassword(e.target.value)} type="password" fullWidth sx={{mt:2}}/>
      <Button variant="contained" sx={{mt:2}} onClick={submit}>Login</Button>
      <Typography sx={{mt:2}}>Don't have an account? <Link to="/register">Register</Link></Typography>
    </>
  )
}
